  &lt;figure class=&#34;highlight html&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;  // 定义全局过滤器datefmt&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  Vue.filter(&amp;#x27;datefmt&amp;#x27;,function(input)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      var date = new Date(input);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      var year = date.getFullYear();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      var m = date.getMonth() + 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      var d = date.getDate();            &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    var fmtStr = year+&amp;#x27;-&amp;#x27;+m +&amp;#x27;-&amp;#x27;+d;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;      return fmtStr; //返回输出结果&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &amp;#125;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;// 调用, 注意datefmt的第一个参数默认就是管道符左边的值&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;tag&#34;&gt;&amp;lt;&lt;span class=&#34;name&#34;&gt;td&lt;/span&gt;&amp;gt;&lt;/span&g